(() => {
  // javascript/slider.js
  (function($) {
    var initializeBlock = function($block) {
      $block.find(".slides").slick({
        arrows: false,
        autoplay: true,
        autoplaySpeed: 5e3,
        centerPadding: "50px",
        cssEase: "ease",
        draggable: false,
        fade: false,
        dots: false,
        infinite: true,
        speed: 1e3,
        slidesToShow: 1,
        centerMode: false,
        variableWidth: false,
        adaptiveHeight: false,
        focusOnSelect: false,
        mobileFirst: true,
        pauseOnHover: false,
        pauseOnFocus: false,
        swipe: false,
        lazyLoad: "ondemand"
      });
    };
    $(document).ready(function() {
      $(".slider").each(function() {
        initializeBlock($(this));
      });
    });
    if (window.acf) {
      window.acf.addAction("render_block_preview/type=slider", initializeBlock);
    }
  })(jQuery);
})();
